// 导入axios
import axios from 'axios'

// 创建一个副本，设置基地址
const http = axios.create({
    baseURL: 'http://localhost:8888/api/private/v1/'
})

//暴露 和接口相关的方法即可
//接口抽取 - 登陆验证接口
export const login = ({username, password}) => {
    return http.post('login', {
        username,
        password
    })
}
